let footerUpBtn = document.querySelector(".footer__up-btn");
window.addEventListener("scroll", () => {
  if (window.scrollY >= contact.offsetTop - contact.offsetHeight) {
    footerUpBtn.classList.add("active");
  } else {
    footerUpBtn.classList.remove("active");
  }
});
let contactContentForm = document.querySelector(".contact__content-form");
let userName = document.querySelector("#userName");
let userSurname = document.querySelector("#userSurname");
let userTel = document.querySelector("#userTel");
let userEmail = document.querySelector("#userEmail");
let userMessage = document.querySelector("#userMessage");
let modal = document.querySelector(".modal");
let contactContentBtn = document.querySelector(".contact__content-btn");
contactContentForm.addEventListener("change", () => {
  if (userName.value !== '' && userSurname.value !== '' && userTel.value !== '' && userEmail.value !== '' && userMessage.value !== '') {
    contactContentBtn.addEventListener("click", () => {
      modal.classList.add("active");
    });
  }
})
modal.addEventListener("click", function (e) {
  if (e.target === e.currentTarget) {
    this.classList.remove("active");
  }
});